set ID=#{Id},SALEPRICE=#{salePrice}, KATEGORIE=#{Kategorie},KGOODSNAME=#{kgoodsName}, CGOODSNAME=#{cgoodsName},EGOODSNAME=#{egoodsName}, JGOODSNAME=#{jgoodsName},KGOODSE=#{kgoodsE}, CGOODSE=#{cgoodsE},EGOODSE=#{egoodsE}, JGOODSE=#{jgoodsE},PRICE=#{Price}
set ID=#{Id},SALEPRICE=#{salePrice}, KATEGORIE=#{Kategorie},KGOODSNAME=#{kgoodsName}, CGOODSNAME=#{cgoodsName},EGOODSNAME=#{egoodsName}, JGOODSNAME=#{jgoodsName},KGOODSE=#{kgoodsE}, CGOODSE=#{cgoodsE},EGOODSE=#{egoodsE}, JGOODSE=#{jgoodsE},IMGNAME=#{imgName},PRICE=#{Price},IMGPATH=#{imgPath}
WHERE ID=#{Id} and goodnum=#{goodNum}
